package ClassConditionArray;

public class IfElseIfMark {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Mark=100;
		
		if (Mark>40) {
			System.out.println("Exam Passed");
		}else if (Mark >=35){
			System.out.println("Exam Just Pass");
		}else {
			System.out.println("Failed");
		}
	}

}